import React, { useState, useEffect } from 'react';
import Filters from '../components/Filter/Filter';
import Product from '../components/Product';
import axios from 'axios';
import './HomePage.css'; // Create CSS file for styling

const HomePage = () => {
  const [products, setProducts] = useState([]);
  const [filter, setFilter] = useState('All'); // Add state for filters

  useEffect(() => {
    // Fetching products using Axios
    axios.get('https://fakestoreapi.com/products')
      .then(response => setProducts(response.data))
      .catch(error => console.log(error));
  }, []);

  const filteredProducts = filter === 'All' ? products : products.filter(product => product.category === filter);

  return (
    <div className="home-container">
      <header>
        <h1>Discover Our Products</h1>
        <p>Lorem ipsum dolor sit amet consectetur.</p>
      </header>

      <Filters filter={filter} setFilter={setFilter} />

      <section className="products">
        {filteredProducts.map((product) => (
          <Product key={product.id} name={product.title} price={product.price} image={product.image} />
        ))}
      </section>

      <footer>
        <p>&copy; 2023 Metta Muse. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default HomePage;
